+document.addEventListener('DOMContentLoaded', () => {
+    const flashAlerts = document.querySelectorAll('.flash-alert');
+
+    flashAlerts.forEach(alert => {
+        const autoDismissAttr = alert.getAttribute('data-auto-dismiss');
+        const timeoutMs = autoDismissAttr ? parseInt(autoDismissAttr, 10) : 4500;
+        const closeButton = alert.querySelector('.btn-close');
+        let dismissTimer;
+        let fadeTimer;
+
+        const startTimers = () => {
+            if (dismissTimer) {
+                clearTimeout(dismissTimer);
+            }
+            if (fadeTimer) {
+                clearTimeout(fadeTimer);
+            }
+
+            fadeTimer = window.setTimeout(() => {
+                alert.classList.add('fade-out');
+            }, Math.max(timeoutMs - 300, 0));
+
+            dismissTimer = window.setTimeout(() => {
+                const bsAlert = bootstrap.Alert.getOrCreateInstance(alert);
+                bsAlert.close();
+            }, timeoutMs);
+        };
+
+        const clearTimers = () => {
+            if (dismissTimer) {
+                clearTimeout(dismissTimer);
+                dismissTimer = null;
+            }
+            if (fadeTimer) {
+                clearTimeout(fadeTimer);
+                fadeTimer = null;
+            }
+        };
+
+        alert.addEventListener('mouseenter', clearTimers);
+        alert.addEventListener('mouseleave', startTimers);
+        alert.addEventListener('touchstart', clearTimers, { passive: true });
+        alert.addEventListener('touchend', startTimers, { passive: true });
+
+        alert.addEventListener('closed.bs.alert', () => {
+            clearTimers();
+            alert.remove();
+        });
+
+        if (closeButton) {
+            closeButton.addEventListener('click', () => {
+                clearTimers();
+            });
+        }
+
+        if (!alert.hasAttribute('data-auto-dismiss-disabled')) {
+            startTimers();
+        }
+    });
+});

